import React from "react";
import "../header/Header.css";
import {
  faBed,
  faCalendarDays,
  faCar,
  faPerson,
  faPlane,
  faTaxi,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useState } from "react";

const Header = () => {
  /// handle calendar
  const [openCalendar, setOpenCalendar] = useState(false);
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);

  /// handle direction
  const SearchHandle = () => {
    location.replace("/search");
  };

  return (
    <div className="header">
      <div className="headerContainer">
        <h1 className="title">A lifetime of discounts? It's Genius.</h1>
        <p className="desc">
          Get rewarded for your travels â€“ unlock instant savings of 10% or more
          with a free account
        </p>
        <button className="searchBtn">Sign in / Register</button>

        <div className="formSearch">
          <div className="formSearchItem">
            <FontAwesomeIcon icon={faBed} className="icon" />
            <input
              type="text"
              placeholder="Where are you going?"
              className="searchInput"
              onChange={(e) => setDestination(e.target.value)}
            />
          </div>

          <div className="formSearchItem">
            <FontAwesomeIcon icon={faCalendarDays} className="icon" />
            <span
              onClick={setOpenCalendar(!openCalendar)}
              className="searchText"
            >
              {`${format(date[0].sta)}`}
            </span>
          </div>

          <div className="formSearchItem">
            <FontAwesomeIcon icon={faPerson} className="icon" />
            <span className="searchText"> 1 dult - 0 children - 1 room</span>
          </div>
          <div className="formSearchItem">
            <button className="searchBtn" onClick={SearchHandle}>
              Search
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
