              <FontAwesomeIcon icon={faLocationDot} className="icon" />
              <span className="address">{props.address}</span>
              <div>
                <span className="distance">{props.distance}</span>
              </div>
              <div>
                <span className="price">{props.price}</span>
              </div>
            </div>
