import React from "react";
import "./SearchList.css";
import searchData from "../../data/search.json";
import SearchListItem from "../../components/searchList/SearchListItem";

const SearchList = () => {
  return (
    <div className="searchList">
      {searchData.map((props, index) => (
        <SearchListItem
          key={index}
          name={props.name}
          distance={props.distance}
          tag={props.tag}
          type={props.type}
          description={props.description}
          price={props.price}
          rate_text={props.rate_text}
          image_url={props.image_url}
        />
      ))}
<div className="searchItem">
        <img src={props.img_url} alt="" className="searchImg" />
        <div className="searchDesc">
          <h1 className="searchTitle">{props.name}</h1>
          <span className="searchDistance">{props.distance} from center</span>
          <span className="searchTaxiOp">{props.tag}</span>
          <span className="searchSubtitle">{props.description}</span>
          <span className="searchFeatures">{props.type}</span>
          {/* If can cancel */}
          {props.free_cancel ? (
            <div>
              <span className="searchCancelOp">Free cancellation </span>
              <span className="searchCancelOpSubtitle">
                You can cancel later, so lock in this great price today!
              </span>
            </div>
          ) : (
            <div></div>
          )}
        </div>
        <div className="searchDetails">
          <div className="searchRating">
            <span>{props.rate_text}</span>
            <button>{props.rate}</button>
          </div>
          <div className="searchDetailTexts">
            <span className="searchPrice">${props.price}</span>
            <span className="searchTaxOp">Includes taxes and fees</span>
            <button className="searchCheckButton">See availability</button>
          </div>
        </div>
      </div>



    </div>
  );
};

export default SearchList;
