import React from "react";
import "../header/Header.css";
import {
  faBed,
  faCalendarDays,
  faCar,
  faPerson,
  faPlane,
  faTaxi,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useState } from "react";

const Header = () => {
  const SearchHandle = () => {
    location.replace("/search");
  };

  const [destination, getDestination] = useState("");
  const [options, setOptions] = useState({
    adult: 1,
    children: 0,
    room: 1,
  });

  return (
    <div className="header">
      <div className="headerContainer">
        <h1 className="headerTitle">A lifetime of discounts? It's Genius.</h1>
        <p className="headerDesc">
          Get rewarded for your travels â€“ unlock instant savings of 10% or more
          with a free account
        </p>
        <button className="headerBtn">Sign in / Register</button>

        <div className="formSearch">
          <div className="formSearchItem">
            <FontAwesomeIcon icon={faBed} className="headerIcon" />
            <input
              type="text"
              placeholder="Where are you going?"
              className="searchInput"
              onChange={(e) => setDestination(e.target.value)}
            />
          </div>

           <div className="headerSearchItem">
                <FontAwesomeIcon icon={faCalendarDays} className="headerIcon" />
                <span className="searchText"></span>
              </div>

             <div className="headerSearchItem">
                <FontAwesomeIcon icon={faPerson} className="headerIcon" />
                <span className="searchText">1 dult - 0 children - 1 room</span>
              </div>
          <div className="searchItem">
            <button className="searchBtn" onClick={SearchHandle}>
              Search
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
